var greet="";
var d = new Date(2021,2,6,21)
var d = d.getHours();

function Sahi(){
    if(d>=1 && d<12){
        greet = "good morning"
    }else if(d>=12 && d<19){
        greet="good afternoon"
    }else{greet="good night"}
    return <p>hello sir,{greet}</p>
    
}
export default Sahi;
